import React, { useEffect, useState } from "react";
import socket from "./socket"; // Importer la connexion WebSocket

function App() {
  const [message, setMessage] = useState(""); // Message √† envoyer
  const [chat, setChat] = useState([]); // Liste des messages re√ßus
  const [room, setRoom] = useState(""); // Nom de la room √† rejoindre
  const [roomName, setRoomName] = useState(""); // Nom de la room √† cr√©er

  useEffect(() => {
    // √âcoute des messages venant du serveur
    socket.on("message", (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    // Nettoyer les √©couteurs lorsque le composant est d√©mont√©
    return () => {
      socket.off("message");
    };
  }, []);

  // Envoyer un message au serveur
  const sendMessage = () => {
    if (message.trim() !== "") {
      socket.emit("sendMessage", { room, message });
      setMessage(""); // R√©initialise l'input apr√®s l'envoi
    }
  };

  // Cr√©er une room
  const createRoom = () => {
    if (roomName.trim() !== "") {
      socket.emit("createRoom", roomName);
      setRoom(roomName); // Rejoindre la room nouvellement cr√©√©e
      setChat([]); // R√©initialiser le chat
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>üí¨ Chat WebSocket</h1>

      {/* Zone de cr√©ation de room */}
      <div>
        <input
          type="text"
          value={roomName}
          onChange={(e) => setRoomName(e.target.value)}
          placeholder="Nom de la room"
          style={{ marginRight: "10px" }}
        />
        <button onClick={createRoom}>Cr√©er une Room</button>
      </div>

      {/* Zone de chat */}
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          height: "300px",
          overflowY: "scroll",
          marginBottom: "20px",
        }}
      >
        {chat.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>

      {/* Zone de saisie du message */}
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Tapez votre message..."
        style={{ width: "80%", marginRight: "10px" }}
      />
      <button onClick={sendMessage}>Envoyer</button>
    </div>
  );
}

export default App;
