import React, { useEffect, useState } from "react";
import socket from "./socket"; // Assure-toi que tu utilises socket.js pour la connexion

function App() {
  const [message, setMessage] = useState(""); // Message √† envoyer
  const [chat, setChat] = useState([]); // Liste des messages re√ßus
  const [roomName, setRoomName] = useState(""); // Nom de la room √† cr√©er
  const [currentRoom, setCurrentRoom] = useState(""); // Room actuelle dans laquelle l'utilisateur est

  useEffect(() => {
    // √âcoute des messages venant du serveur
    socket.on("message", (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    // Nettoyer les √©couteurs lorsque le composant est d√©mont√©
    return () => {
      socket.off("message");
    };
  }, []);

  // Envoyer un message au serveur
  const sendMessage = () => {
    if (message.trim() !== "") {
      socket.emit("sendMessage", { message, room: currentRoom }); // Envoie le message dans la room actuelle
      setMessage(""); // R√©initialise l'input apr√®s l'envoi
    }
  };

  // Cr√©er une room et rejoindre la room
  const createRoom = () => {
    if (roomName.trim() !== "") {
      socket.emit("createRoom", roomName); // √âv√©nement WebSocket pour cr√©er la room
      setCurrentRoom(roomName); // Met la room actuelle √† celle que l'utilisateur vient de cr√©er
      setRoomName(""); // R√©initialiser le champ du nom de la room
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>üí¨ Chat WebSocket</h1>

      {/* Section pour cr√©er une room */}
      <div style={{ marginBottom: "20px" }}>
        <h3>Cr√©er un salon</h3>
        <input
          type="text"
          value={roomName}
          onChange={(e) => setRoomName(e.target.value)}
          placeholder="Nom du salon"
          style={{ width: "80%", marginRight: "10px" }}
        />
        <button onClick={createRoom}>Cr√©er</button>
      </div>

      {/* Affichage de la room actuelle */}
      {currentRoom && <h3>Vous √™tes dans le salon: {currentRoom}</h3>}

      {/* Affichage des messages */}
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          height: "300px",
          overflowY: "scroll",
          marginBottom: "20px",
        }}
      >
        {chat.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>

      {/* Zone pour envoyer des messages */}
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Tapez votre message..."
        style={{ width: "80%", marginRight: "10px" }}
      />
      <button onClick={sendMessage}>Envoyer</button>
    </div>
  );
}

export default App;
