import React, { useEffect, useState } from "react";
import socket from "./socket";  // Assure-toi que tu as bien ton fichier socket.js

function App() {
  const [message, setMessage] = useState("");  // Message √† envoyer
  const [chat, setChat] = useState([]);  // Liste des messages re√ßus
  const [roomName, setRoomName] = useState("");  // Room √† cr√©er ou rejoindre
  const [isInRoom, setIsInRoom] = useState(false);  // V√©rifier si l'utilisateur est dans une room
  const [userName, setUserName] = useState("");  // Pseudo de l'utilisateur
  const [isNameSet, setIsNameSet] = useState(false);  // V√©rifier si l'utilisateur a d√©fini un pseudo

  useEffect(() => {
    // √âcoute des messages venant du serveur
    socket.on("message", (data) => {
      setChat((prevChat) => [...prevChat, data]);  // Ajouter le message dans le chat
    });

    // Nettoyer les √©couteurs lorsque le composant est d√©mont√©
    return () => {
      socket.off("message");
    };
  }, []);

  // Fonction pour envoyer un message et g√©rer les commandes
  const sendMessage = () => {
    if (message.trim() !== "") {
      if (message.startsWith("/createRoom")) {
        const room = message.split(" ")[1];  // Extraire le nom de la room apr√®s "/createRoom"
        if (room) {
          socket.emit("createRoom", room);  // Cr√©er la room
          setRoomName(room);  // D√©finir le nom de la room actuelle
          setIsInRoom(true);  // L'utilisateur est dans la room
          setChat((prevChat) => [...prevChat, { userName: "System", message: `Vous avez cr√©√© la room '${room}' !` }]);  // Afficher un message dans le chat
          setMessage("");  // R√©initialiser l'input
        } else {
          setChat((prevChat) => [...prevChat, "Erreur: veuillez sp√©cifier un nom de room."]);
        }
      } else if (message.startsWith("/joinRoom")) {
        const room = message.split(" ")[1];  // Extraire le nom de la room apr√®s "/joinRoom"
        if (room) {
          socket.emit("joinRoom", room);  // Rejoindre la room
          setRoomName(room);  // D√©finir le nom de la room actuelle
          setIsInRoom(true);  // L'utilisateur est dans la room
          setChat((prevChat) => [...prevChat, { userName: "System", message: `Vous avez rejoint la room '${room}' !` }]);  // Afficher un message dans le chat
          setMessage("");  // R√©initialiser l'input
        } else {
          setChat((prevChat) => [...prevChat, "Erreur: veuillez sp√©cifier une room √† rejoindre."]);
        }
      } else if (message.startsWith("/changePseudo")) {
        const newPseudo = message.split(" ")[1];  // Extraire le nouveau pseudo
        if (newPseudo) {
          setUserName(newPseudo);  // Changer le pseudo localement
          setChat((prevChat) => [
            ...prevChat,
            { userName: "System", message: `Votre pseudo a √©t√© chang√© en '${newPseudo}'` }
          ]);  // Afficher un message indiquant le changement de pseudo
          setMessage("");  // R√©initialiser l'input
        } else {
          setChat((prevChat) => [
            ...prevChat,
            { userName: "System", message: "Erreur: veuillez sp√©cifier un nouveau pseudo." }
          ]);  // Si aucun nouveau pseudo n'est sp√©cifi√©
        }
      } else {
        // Si ce n'est pas une commande, envoyer le message normal avec le pseudo
        if (roomName.trim() !== "") {
          socket.emit("sendMessage", { room: roomName, message, userName });  // Envoyer le message avec le pseudo
          setMessage("");  // R√©initialiser l'input apr√®s l'envoi
        }
      }
    }
  };




  // Fonction pour d√©finir le pseudo de l'utilisateur
  const setUserNameHandler = () => {
    if (userName.trim() !== "") {
      setIsNameSet(true);  // Le pseudo est d√©fini
    } else {
      alert("Veuillez entrer un pseudo.");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>üí¨ Chat WebSocket</h1>

      {/* √âtape pour d√©finir le pseudo */}
      {!isNameSet && (
        <div>
          <input
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            placeholder="Choisissez un pseudo"
            style={{ width: "80%", marginRight: "10px" }}
          />
          <button onClick={setUserNameHandler}>D√©finir le pseudo</button>
        </div>
      )}

      {/* Champ pour entrer le nom de la room */}
      {isNameSet && !isInRoom && (
        <div>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Tapez /createRoom <nom de la room> ou /joinRoom <nom de la room>"
            style={{ width: "80%", marginRight: "10px" }}
          />
          <button onClick={sendMessage}>Envoyer</button>
        </div>
      )}

<div
  style={{
    border: "1px solid #ccc",
    padding: "10px",
    height: "300px",
    overflowY: "scroll",
    marginBottom: "20px",
  }}
>
  {chat.map((msg, index) => {
    console.log(msg); // Affiche msg dans la console pour v√©rifier sa structure
    return (
      <p key={index}>
        <strong>{msg.userName }</strong>: {msg.message}
      </p>
    );
  })}
</div>



      {/* Input pour envoyer un message lorsque dans une room */}
      {isInRoom && (
        <div>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Tapez votre message..."
            style={{ width: "80%", marginRight: "10px" }}
          />
          <button onClick={sendMessage}>Envoyer</button>
        </div>
      )}
    </div>
  );
}

export default App;
