import { useEffect, useState } from "react";
import io from "socket.io-client";

const socket = io("http://localhost:4000"); // Connexion au serveur

const Chat = () => {
  const [message, setMessage] = useState("");
  const [messages, setMessages] = useState([]);
  const [pseudo, setPseudo] = useState("");
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Écoute de l'événement pour mettre à jour le pseudo
    socket.on("updatePseudo", (newPseudo) => {
      setPseudo(newPseudo);
    });

    // Récupération de la liste des utilisateurs (optionnel)
    socket.on("message", (data) => {
      setMessages((prevMessages) => [...prevMessages, data]);
    });

    return () => {
      socket.off("updatePseudo");
      socket.off("message");
    };
  }, []);

  const handleSendMessage = () => {
    if (message) {
      socket.emit("sendMessage", { message, userName: pseudo, room: "room1" });
      setMessage("");
    }
  };

  const handleChoosePseudo = () => {
    if (pseudo) {
      socket.emit("choosePseudo", pseudo);
    }
  };

  const handleChangePseudo = (newPseudo) => {
    setPseudo(newPseudo);
    socket.emit("changePseudo", newPseudo);
  };

  return (
    <div>
      <div>
        <input
          type="text"
          placeholder="Choisir un pseudo"
          value={pseudo}
          onChange={(e) => setPseudo(e.target.value)}
        />
        <button onClick={handleChoosePseudo}>Choisir Pseudo</button>
      </div>

      <div>
        <input
          type="text"
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          placeholder="Envoyer un message"
        />
        <button onClick={handleSendMessage}>Envoyer</button>
      </div>

      <div>
        {messages.map((msg, index) => (
          <div key={index}>
            <strong>{msg.userName}:</strong> {msg.message}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Chat;
