import React, { useEffect, useState } from "react";
import socket from "./socket";  // Ton fichier socket.js

function App() {
  const [message, setMessage] = useState("");  // Message Ã  envoyer
  const [chat, setChat] = useState([]);  // Liste des messages reÃ§us
  const [roomName, setRoomName] = useState("");  // Room Ã  crÃ©er ou rejoindre
  const [isInRoom, setIsInRoom] = useState(false);  // VÃ©rifier si l'utilisateur est dans une room

  useEffect(() => {
    // VÃ©rifier la connexion WebSocket
    socket.on("connect", () => {
      console.log("ConnectÃ© au serveur WebSocket", socket.id);  // VÃ©rifier la connexion
    });

    // Ã‰coute des messages venant du serveur
    socket.on("message", (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    // Nettoyer les Ã©couteurs lorsque le composant est dÃ©montÃ©
    return () => {
      socket.off("message");
      socket.off("connect");
    };
  }, []);

  // CrÃ©er une room
  const createRoom = () => {
    if (roomName.trim() !== "") {
      socket.emit("createRoom", roomName);
      setIsInRoom(true);  // L'utilisateur est maintenant dans la room
      setChat([]);  // RÃ©initialise le chat
    }
  };

  // Rejoindre une room
  const joinRoom = () => {
    if (roomName.trim() !== "") {
      socket.emit("joinRoom", roomName);  // Ã‰mettre un Ã©vÃ©nement pour rejoindre la room
      setIsInRoom(true);
      setChat([]);  // RÃ©initialise le chat
    }
  };

  // Envoyer un message
  const sendMessage = () => {
    if (message.trim() !== "" && roomName.trim() !== "") {
      socket.emit("sendMessage", { room: roomName, message });
      setMessage("");  // RÃ©initialise l'input aprÃ¨s l'envoi
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>ðŸ’¬ Chat WebSocket</h1>

      {/* Champ pour entrer le nom de la room */}
      <div>
        <input
          type="text"
          value={roomName}
          onChange={(e) => setRoomName(e.target.value)}
          placeholder="Nom de la room..."
          style={{ width: "80%", marginRight: "10px" }}
        />
        <button onClick={createRoom}>CrÃ©er Room</button>
        <button onClick={joinRoom}>Rejoindre Room</button>
      </div>

      {/* Liste des messages */}
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          height: "300px",
          overflowY: "scroll",
          marginBottom: "20px",
        }}
      >
        {chat.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>

      {/* Input pour envoyer un message */}
      {isInRoom && (
        <div>
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Tapez votre message..."
            style={{ width: "80%", marginRight: "10px" }}
          />
          <button onClick={sendMessage}>Envoyer</button>
        </div>
      )}
    </div>
  );
}

export default App;
