import logo from './logo.svg';
import React, { useEffect, useState } from "react";
import './App.css';

import socket from "./socket";

function App() {
  const [message, setMessage] = useState(""); // Message Ã  envoyer
  const [chat, setChat] = useState([]); // Liste des messages reÃ§us

  useEffect(() => {
    // Ã‰coute des messages venant du serveur
    socket.on("message", (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    // Nettoyer les Ã©couteurs lorsque le composant est dÃ©montÃ©
    return () => {
      socket.off("message");
    };
  }, []);

  // Envoyer un message au serveur
  const sendMessage = () => {
    if (message.trim() !== "") {
      socket.emit("sendMessage", message);
      setMessage(""); // RÃ©initialise l'input aprÃ¨s l'envoi
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h1>ðŸ’¬ Chat WebSocket</h1>
      <div
        style={{
          border: "1px solid #ccc",
          padding: "10px",
          height: "300px",
          overflowY: "scroll",
          marginBottom: "20px",
        }}
      >
        {chat.map((msg, index) => (
          <p key={index}>{msg}</p>
        ))}
      </div>
      <input
        type="text"
        value={message}
        onChange={(e) => setMessage(e.target.value)}
        placeholder="Tapez votre message..."
        style={{ width: "80%", marginRight: "10px" }}
      />
      <button onClick={sendMessage}>Envoyer</button>
    </div>
  );
}

export default App;
