import React, { useEffect, useState } from "react";
import socket from "./socket";

function App() {
  const [message, setMessage] = useState("");
  const [chat, setChat] = useState([]);
  const [roomName, setRoomName] = useState("");
  const [isInRoom, setIsInRoom] = useState(false);
  const [userName, setUserName] = useState("");
  const [isNameSet, setIsNameSet] = useState(false);

  useEffect(() => {
    socket.on("message", (data) => {
      setChat((prevChat) => [...prevChat, data]);
    });

    return () => {
      socket.off("message");
    };
  }, []);

  const sendMessage = () => {
    if (message.trim() !== "") {
      if (message.startsWith("/createRoom")) {
        const room = message.split(" ")[1];
        if (room) {
          socket.emit("createRoom", room);
          setRoomName(room);
          setIsInRoom(true);
          setChat([]);
          setMessage("");
        } else {
          setChat((prevChat) => [...prevChat, "Erreur: veuillez spÃ©cifier un nom de room."]);
        }
      } else if (message.startsWith("/joinRoom")) {
        const room = message.split(" ")[1];
        if (room) {
          socket.emit("joinRoom", room);
          setRoomName(room);
          setIsInRoom(true);
          setChat([]);
          setMessage("");
        } else {
          setChat((prevChat) => [...prevChat, "Erreur: veuillez spÃ©cifier une room Ã  rejoindre."]);
        }
      } else {
        if (roomName.trim() !== "") {
          socket.emit("sendMessage", { room: roomName, message, userName });
          setMessage("");
        }
      }
    }
  };

  const setUserNameHandler = () => {
    if (userName.trim() !== "") {
      setIsNameSet(true);
    } else {
      alert("Veuillez entrer un pseudo.");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-800 to-gray-900 flex flex-col justify-center items-center p-6">
      <h1 className="text-3xl text-white font-semibold mb-6">ðŸ’¬ Chat WebSocket</h1>

      {/* Ã‰tape pour dÃ©finir le pseudo */}
      {!isNameSet && (
        <div className="flex items-center mb-4">
          <input
            type="text"
            value={userName}
            onChange={(e) => setUserName(e.target.value)}
            placeholder="Choisissez un pseudo"
            className="px-4 py-2 rounded-lg text-black w-80 focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          <button
            onClick={setUserNameHandler}
            className="ml-4 px-6 py-2 rounded-lg bg-blue-500 text-white hover:bg-blue-600 focus:outline-none"
          >
            DÃ©finir
          </button>
        </div>
      )}

      {/* Champ pour entrer le nom de la room */}
      {isNameSet && !isInRoom && (
        <div className="mb-6 w-full max-w-3xl flex items-center">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Tapez /createRoom <nom de la room> ou /joinRoom <nom de la room>"
            className="px-4 py-2 rounded-lg text-black w-80 focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          <button
            onClick={sendMessage}
            className="ml-4 px-6 py-2 rounded-lg bg-blue-500 text-white hover:bg-blue-600 focus:outline-none"
          >
            Envoyer
          </button>
        </div>
      )}

      {/* Liste des messages */}
      <div
        className="border border-gray-600 bg-gray-800 text-white rounded-lg p-4 mb-6 w-full max-w-3xl h-80 overflow-y-scroll"
      >
        {chat.map((msg, index) => (
          <p key={index} className="mb-2 text-sm">
            <span className="font-semibold">{msg.userName}</span>: {msg.message}
          </p>
        ))}
      </div>

      {/* Input pour envoyer un message lorsque dans une room */}
      {isInRoom && (
        <div className="mb-6 w-full max-w-3xl flex items-center">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Tapez votre message..."
            className="px-4 py-2 rounded-lg text-black w-80 focus:outline-none focus:ring-2 focus:ring-blue-400"
          />
          <button
            onClick={sendMessage}
            className="ml-4 px-6 py-2 rounded-lg bg-blue-500 text-white hover:bg-blue-600 focus:outline-none"
          >
            Envoyer
          </button>
        </div>
      )}
    </div>
  );
}

export default App;
